public class ChartBuilder
{
    /// <summary>
    /// Будує графік: X = година доби (0–23), Y = час проведений у годинах.
    /// Порожні години заповнюються нулями.
    /// </summary>
    public void BuildTimeSpentByHourChartSwapped(List<UrlData> urlDataList)
    {
        // Фільтрація по поточній даті (наприклад, 07.05.2025)
        var today = DateTime.Today;
        var todayData = urlDataList
            .Where(d => d.Timestamp.Date == today)
            .ToList();

        // Підготовка даних: для кожної години 0..23
        var hoursOfDay = Enumerable.Range(0, 24).ToList();
        var spentHours = hoursOfDay
            .Select(h =>
                todayData.Where(d => d.Timestamp.Hour == h).Sum(d => d.TimeSpent)
                / 3600.0
            )
            .ToList();

        // Створюємо PlotView
        var plotView = new PlotView
        {
            Dock = DockStyle.None,
            Size = new Size(600, 400),
            Location = new Point(225, 150)
        };

