    // Параметр: список всіх даних UrlData
    public void BuildTimeSpentByHourChart(List<UrlData> urlDataList)
    {
        // 1) Фільтрація за сьогоднішньою датою
        var today = DateTime.Today; // у вашому випадку — 07.05.2025
        var todayData = urlDataList
            .Where(d => d.Timestamp.Date == today)
            .ToList();

        // 2) Підготовка точок: для кожної години 0..23 обчислюємо сумарний TimeSpent
        var xValues = new List<double>();
        var yValues = new List<int>();
        for (int hour = 0; hour < 24; hour++)
        {
            int totalSeconds = todayData
                .Where(d => d.Timestamp.Hour == hour)
                .Sum(d => d.TimeSpent);

            // Переводимо в години з дробом
            double hoursSpent = totalSeconds / 3600.0;
            xValues.Add(hoursSpent);
            yValues.Add(hour);
        }

        // 3) Налаштування PlotView і PlotModel
        var plotView = new PlotView
        {
