        .Select(r =>
        {
            DateTime start = r.Timestamp;
            DateTime stop = start.AddSeconds(r.TimeSpent); // TimeSpent в секундах
            return (Start: start, Stop: stop);
        })
        .OrderBy(p => p.Start)
