    {
        // X — номер 15-хв інтервалу; Y — хвилини
        // Формат підписи: години та хвилини
        double xValue = i;
        series.Points.Add(new DataPoint(xValue, merged[i]));
    }
    model.Series.Add(series);

    // Ось X — 15-хв інтервали доби
    model.Axes.Add(new LinearAxis
    {
        Position = AxisPosition.Bottom,
