        Title = "Хвилини",
        Minimum = 0,
        Maximum = maxY,
        MajorStep = stepY,
        MinorStep = stepY / 2,
        TitleColor = OxyColor.FromRgb(159, 183, 213),
        TextColor = OxyColor.FromRgb(159, 183, 213),
        MajorGridlineStyle = LineStyle.Solid,
        MinorGridlineStyle = LineStyle.Dot,
        MajorGridlineColor = OxyColor.FromRgb(81, 99, 119),
