    };

    for (int i = 0; i < 96; i++)
    {
        series.Points.Add(new DataPoint(i, merged[i]));
    }

    model.Series.Add(series);

    // Ось X — 15-хв інтервали доби
    model.Axes.Add(new LinearAxis
    {
        Position = AxisPosition.Bottom,
        Title = "Час",
        Minimum = -0.5,
