private (DateTime, DateTime)? ConvertSessionToTimes(Session s, DateTime date)
{
    if (!DateTime.TryParseExact(s.Start, "HH:mm:ss", CultureInfo.InvariantCulture,
        DateTimeStyles.None, out var t1)) return null;

    if (!DateTime.TryParseExact(s.Stop, "HH:mm:ss", CultureInfo.InvariantCulture,
        DateTimeStyles.None, out var t2)) return null;

    var dt1 = date.Add(t1.TimeOfDay);
    var dt2 = date.Add(t2.TimeOfDay);

    if (dt2 < dt1) dt2 = dt2.AddDays(1);
    return (dt1, dt2);
}


