    var totalDays = maximumX - minimumX;
    double step;
    if (totalDays <= 10)
        step = 1;
    else if (totalDays <= 30)
        step = 2;
    else if (totalDays <= 90)
        step = 7;
    else if (totalDays <= 365)
        step = 30;
    else
        step = 90;

    return new DateTimeAxis
    {
        Position = AxisPosition.Bottom,
        Title = title,
        Minimum = minimumX,
        Maximum = maximumX,
        StringFormat = "dd.MM",
        MajorStep = step,
        Angle = 45,
