        var results = new List<SimilarityResult>();

        foreach (var proj in projects)
        {
            foreach (var page in pages)
            {
                var userMessage = new StringBuilder()
                    .AppendLine("Порівняй тематику двох текстів і дай відповідь лише “Схожість виявлено” або “Схожість не виявлено”.")
                    .AppendLine()
                    .AppendLine("Текст проекту:")
                    .AppendLine(proj.Response)
                    .AppendLine()
                    .AppendLine("Текст веб-сторінки:")
                    .AppendLine(page.Response)
                    .ToString();

                var response = SendMessageToAI(userMessage, out string similarity);
                if (!response)
                    similarity = $"Error";

                results.Add(new SimilarityResult
                {
                    Project = proj.Project,
                    Url = page.Url,
                    Similarity = similarity.Replace("\"", "").Trim()
                });

                if (response)
                    _requestsSentToday++;
            }
        }

        return SaveResultToFile(outputJsonPath, results);
    }
    catch (Exception ex)
    {
        return $"Загальна помилка: {ex.Message}";
    }
}

private bool SendMessageToAI(string userMessage, out string responseContent)
{
    try
    {
        var payload = new
        {
            model = "command-r-plus-08-2024",
            messages = new[] { new { role = "user", content = userMessage } }
        };

        var jsonPayload = JsonSerializer.Serialize(payload);
        var content = new StringContent(jsonPayload, Encoding.UTF8, "application/json");
        var response = _client.PostAsync(Url, content).Result;

        if (response.IsSuccessStatusCode)
        {
            responseContent = response.Content.ReadAsStringAsync().Result;
            return true;
        }
        else
        {
            responseContent = $"{response.StatusCode} - {response.Content.ReadAsStringAsync().Result}";
            return false;
        }
    }
    catch (Exception ex)
    {
        responseContent = $"Помилка надсилання: {ex.Message}";
        return false;
    }
}

private void LogError(string identifier, string errorMessage, List<object> resultList)
{
    if (Uri.IsWellFormedUriString(identifier, UriKind.Absolute))
        resultList.Add(new { Url = identifier, Error = errorMessage });
    else
        resultList.Add(new { Project = identifier, Error = errorMessage });

    Console.WriteLine($"Помилка: {identifier} - {errorMessage}");
}

private string SaveResultToFile(string path, object data)
{
    File.WriteAllText(path, JsonSerializer.Serialize(data, new JsonSerializerOptions { WriteIndented = true }));
    return $"Обробка завершена. Результати збережено в {path}";
}

