    this.Controls.Add(viewport);

    // 2) –í–Ω—É—Ç—Ä—ñ—à–Ω—ñ–π contentPanel
    contentPanel = new Panel
    {
        Name = "contentPanel",
        Location = new Point(0, 0),
        AutoSize = true,
        AutoSizeMode = AutoSizeMode.GrowAndShrink
    };
    viewport.Controls.Add(contentPanel);

    // –©–æ–± UpdateScrollBar() –≤–∏–∫–ª–∏–∫–∞–≤—Å—è, –∫–æ–ª–∏ –¥–æ–¥–∞—é—Ç—å—Å—è/–≤–∏–¥–∞–ª—è—é—Ç—å—Å—è –∫–æ–Ω—Ç—Ä–æ–ª—ñ:
    contentPanel.ControlAdded += (s, e) => UpdateScrollBar();
    contentPanel.ControlRemoved += (s, e) => UpdateScrollBar();

    // 3) –í–µ—Ä—Ç–∏–∫–∞–ª—å–Ω–∏–π —Å–∫—Ä–æ–ª–±–∞—Ä
    vScrollBar1 = new VScrollBar
    {
        Minimum = 0,
        SmallChange = 20,
