// Поля
private bool isDragging = false;
private int dragOffsetY = 0;

// 1) Початок перетягування
panelThumb.MouseDown += (s, e) => {
    isDragging    = true;
    dragOffsetY   = e.Y;
};
// 2) Кінець перетягування
panelTrack.MouseUp += (s, e) => {
    isDragging = false;
};
// 3) Під час руху миші
panelTrack.MouseMove += (s, e) => {
    if (!isDragging) return;

    // Нове Y для thumb
    int newY = e.Y - dragOffsetY;
    // Межі руху
    newY = Math.Max(0, Math.Min(newY, panelTrack.Height - panelThumb.Height));
    panelThumb.Top = newY;

    // Вираховуємо співвідношення
    float ratio      = newY / (float)(panelTrack.Height - panelThumb.Height);
    int maxScroll    = contentPanel.Height - viewport.Height;
    contentPanel.Top = -(int)(ratio * maxScroll);
};

// 4) Колесо миші (коли фокус у viewport або panelTrack)
this.MouseWheel += (s, e) => {
    // Зрушуємо thumb
    int deltaThumb = (int)(-e.Delta / 120f * 20); 
    int newTop     = panelThumb.Top + deltaThumb;
    newTop = Math.Max(0, Math.Min(newTop, panelTrack.Height - panelThumb.Height));
    panelThumb.Top = newTop;

    // Відповідна прокрутка контенту
    float ratio      = newTop / (float)(panelTrack.Height - panelThumb.Height);
    int maxScroll    = contentPanel.Height - viewport.Height;
    contentPanel.Top = -(int)(ratio * maxScroll);
};


ratio — це відношення позиції ползунка до максимальної можливості руху.

maxScroll — максимальне зміщення контенту (різниця висот contentPanel – viewport).

