        return $"{sec} с";
    }
    else if (_currentUnitLabel == "хв.")
    {
        // rawValue тут — це кількість хвилин (double).
        int min = (int)Math.Round(rawValue);
        return $"{min} хв.";
    }
    else // "год."
    {
        // rawValue тут — це кількість годин (double).
        // Якщо геть ціле — показуємо без дробової частини:
        if (Math.Abs(rawValue - Math.Round(rawValue)) < 0.0001)
