private void buildDailyChart<T>(
    List<T> dataList,
    DateTime selectedDate,
    Func<T, (DateTime Start, DateTime Stop)?> convertToInterval)
{
    // 1) Лише дата без часу
    DateTime today = selectedDate.Date;

    // 2) Розпарсимо інтервали Start/Stop
    var parsed = dataList
        .Select(convertToInterval)
        .Where(interval => interval.HasValue && interval.Value.Start != DateTime.MinValue)
        .Select(interval => interval.Value)
        .ToList();

    // 3) Рахуємо рівень активності (у хвилинах) по годинах
    double[] minutesPerHour = new double[24];
    foreach (var interval in parsed)
    {
        DateTime start = interval.Start;
        DateTime stop = interval.Stop;

        // Якщо інтервал повністю поза сьогоднішнім днем — пропускаємо
        if (stop < today || start >= today.AddDays(1))
            continue;

        // Обрізаємо інтервал до меж поточного дня
        if (start < today)
            start = today;
        if (stop > today.AddDays(1))
            stop = today.AddDays(1);

        // Розбиваємо інтервал помодульно по годинах
        while (start < stop)
        {
            int hour = start.Hour;
            DateTime nextHour = new DateTime(start.Year, start.Month, start.Day, hour, 0, 0).AddHours(1);
            DateTime segmentEnd = (stop < nextHour) ? stop : nextHour;

            double minutes = (segmentEnd - start).TotalMinutes;
            minutesPerHour[hour] += minutes;

            start = segmentEnd;
        }
    }

    // 4) Визначаємо “масштаб” і “єдину одиницю” (сек/хв/год)
    double rawMax = minutesPerHour.Max(); // максимум у хвилинах

    double scaleFactor;
    string unitLabel;

    if (rawMax < 1.0)
    {
        // Якщо <1 хвилини, показуємо в секундах
        scaleFactor = 60.0;  // хв → сек
        unitLabel = "с";
    }
    else if (rawMax < 60.0)
    {
        // Якщо <60 хв, залишаємо в хвилинах
        scaleFactor = 1.0;   // вже в хвилинах
        unitLabel = "хв.";
    }
    else
    {
        // Якщо ≥60 хв, переводимо в години
        scaleFactor = 1.0 / 60.0; // хв → год
        unitLabel = "год.";
    }

    // 5) Розраховуємо максимум у вибраній одиниці
    double maxInUnit = rawMax * scaleFactor;
    // Знімаємо зайве +1, щоб вищий тик точно відповідав фактичному максимуму (або трохи більше)
    double maxY = Math.Ceiling(maxInUnit);
    // Якщо вийшло maxY == 0 (наприклад, уся активність < 1 одиниці), все одно ставимо хоча б 1
    if (maxY <= 0)
        maxY = 1;

    // 6) Обчислюємо MajorStep/MinorStep вже під новий діапазон
    double majorY = Math.Max(1.0, Math.Ceiling(maxY / 10.0));
    double minorY = majorY / 2.0;

    // 7) Створюємо PlotView і PlotModel
    var plotView = CreatePlotView();
    var plotModel = plotView.Model;

    // 8) Серія з точками (X = година, Y = value * scaleFactor)
    var series = new LineSeries
    {
        MarkerType = MarkerType.None,
        Color = Form1.settings.ColorTheme == "dark"
                    ? OxyColor.FromRgb(159, 183, 213)
                    : OxyColor.FromRgb(82, 82, 82),
        StrokeThickness = 2
    };

    // Вимикаємо звичайний клік (щоб не zoom/pan)
    var ctrl = plotView.Controller ?? plotView.ActualController;
    ctrl.UnbindMouseDown(
        OxyPlot.OxyMouseButton.Left,
        OxyPlot.OxyModifierKeys.None,
        clickCount: 1
    );
    plotView.Controller = ctrl;

    // Додаємо точки
    for (int h = 0; h < 24; h++)
    {
        double yValue = minutesPerHour[h] * scaleFactor;
        series.Points.Add(new DataPoint(h, yValue));
    }
    plotModel.Series.Add(series);

    // 9) Вісь X (як було раніше)
    var axisX = new LinearAxis
    {
        Position = AxisPosition.Bottom,
        Title = "Година",
        Minimum = -0.5,
        Maximum = 23.5,
        MajorStep = 1,
        MinorStep = 1,
        LabelFormatter = v => TimeSpan.FromHours(v).ToString(@"hh\:mm"),
        Angle = -45
    };
    StyleAxis(axisX);
    plotModel.Axes.Add(axisX);

    // 10) Вісь Y (лише одна одиниця, без «пустих» верхніх значень)
    var axisY = new LinearAxis
    {
        Position = AxisPosition.Left,
        Title = "Час",
        Minimum = 0,
        Maximum = maxY,
        MajorStep = majorY,
        MinorStep = minorY,
        LabelFormatter = value =>
        {
            // value тут—це в обраній одиниці (секундах чи хвилинах чи годинах).
            if (unitLabel == "с")
            {
                int sec = (int)Math.Round(value);
                return $"{sec} с";
            }
            else if (unitLabel == "хв.")
            {
                int min = (int)Math.Round(value);
                return $"{min} хв.";
            }
            else // "год."
            {
                // Заокруглюємо максимум до двох знаків після коми
                return $"{value:F2} год.";
            }
        }
    };
    StyleAxis(axisY);
    plotModel.Axes.Add(axisY);

    // 11) Намалювати сітку: вертикальні лінії на кожну годину
    for (int h = 0; h < 24; h++)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Vertical,
            X = h,
            Color = Form1.settings.ColorTheme == "dark"
                        ? OxyColor.FromRgb(81, 99, 119)
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = 1.5,
            LineStyle = LineStyle.Solid
        });
    }

    // Горизонтальні лінії від 0 до maxY з кроком minorY
    for (double y = 0; y <= maxY; y += minorY)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Horizontal,
            Y = y,
            Color = Form1.settings.ColorTheme == "dark"
                        ? OxyColor.FromRgb(81, 99, 119)
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = (y % majorY == 0) ? 1.5 : 0.5,
            LineStyle = (y % majorY == 0) ? LineStyle.Solid : LineStyle.Dot
        });
    }

    // 12) Додаємо label9 (підпис «Усього витрачено» – можна заповнювати за потреби)
    this.label9 = CreateMainLabel("label9", "label9", 545, 550, new Size(530, 50));

    // 13) Підʼєднюємо Model та додаємо PlotView у контролси
    plotView.Model = plotModel;
    this.Controls.Add(plotView);
}

