private void buildDailyChart<T>(
    List<T> dataList,
    DateTime selectedDate,
    Func<T, (DateTime Start, DateTime Stop)?> convertToInterval)
{
    // 1) Беремо лише дату без часу
    DateTime today = selectedDate.Date;

    // 2) Парсимо всі інтервали роботи/активності
    var parsed = dataList
        .Select(convertToInterval)
        .Where(interval => interval.HasValue && interval.Value.Start != DateTime.MinValue)
        .Select(interval => interval.Value)
        .ToList();

    // 3) Рахуємо, скільки хвилин припадає на кожну годину обраного дня
    double[] minutesPerHour = new double[24];
    foreach (var interval in parsed)
    {
        DateTime start = interval.Start;
        DateTime stop = interval.Stop;

        // Ігноруємо інтервали, які взагалі не належать до сьогоднішньої дати
        if (stop < today || start >= today.AddDays(1))
            continue;

        // Обрізаємо інтервал до меж поточного дня
        if (start < today) 
            start = today;
        if (stop > today.AddDays(1)) 
            stop = today.AddDays(1);

        // Розбиваємо довгий інтервал на шматки по годинах
        while (start < stop)
        {
            int hour = start.Hour;
            DateTime nextHour = new DateTime(
                start.Year, start.Month, start.Day, hour, 0, 0
            ).AddHours(1);
            DateTime segmentEnd = (stop < nextHour) ? stop : nextHour;

            double minutes = (segmentEnd - start).TotalMinutes;
            minutesPerHour[hour] += minutes;

            start = segmentEnd;
        }
    }

    // 4) Визначаємо, у якій одиниці будемо показувати вісь Y: секунди, хвилини чи години
    double rawMax = minutesPerHour.Max(); // макс. значення в хвилинах

    double scaleFactor;
    string unitLabel;

    if (rawMax < 1.0)
    {
        // Якщо максимум < 1 хвилини → переводимо все в секунди
        scaleFactor = 60.0;  // хвилини * 60 = секунди
        unitLabel = "с";
    }
    else if (rawMax < 60.0)
    {
        // Якщо максимум < 60 хвилин → залишаємо в хвилинах
        scaleFactor = 1.0;   // просто хвилини
        unitLabel = "хв.";
    }
    else
    {
        // Якщо максимум ≥ 60 хвилин → переводимо все в години
        scaleFactor = 1.0 / 60.0; // хвилини / 60 = години
        unitLabel = "год.";
    }

    // 5) Обчислюємо максимальне значення в обраній одиниці
    double maxInUnit = rawMax * scaleFactor;
    double maxY = Math.Ceiling(maxInUnit);
    if (maxY <= 0)
        maxY = 1;

    // 6) Визначаємо кроки Major/Minor для осі Y
    double majorY = Math.Max(1.0, Math.Ceiling(maxY / 10.0));
    double minorY = majorY / 2.0;

    // 7) Створюємо PlotView та отримуємо PlotModel
    var plotView = CreatePlotView();
    var plotModel = plotView.Model;

    // 8) Налаштовуємо серію
    var series = new LineSeries
    {
        MarkerType = MarkerType.None,
        Color = Form1.settings.ColorTheme == "dark"
                    ? OxyColor.FromRgb(159, 183, 213)
                    : OxyColor.FromRgb(82, 82, 82),
        StrokeThickness = 2
    };

    // 9) Додаємо точки: X = година, Y = minutesPerHour[h] * scaleFactor
    for (int h = 0; h < 24; h++)
    {
        double yValue = minutesPerHour[h] * scaleFactor;
        series.Points.Add(new DataPoint(h, yValue));
    }

    // 10) Додаємо TrackerFormatString, щоб при наведенні показувалося значення
    series.TrackerFormatString = $"Година: {{2:0}}\nЧас: {{4}}";

    plotModel.Series.Add(series);

    // 11) Вісь X (залишаємо як було)
    var axisX = new LinearAxis
    {
        Position = AxisPosition.Bottom,
        Title = "Година",
        Minimum = -0.5,
        Maximum = 23.5,
        MajorStep = 1,
        MinorStep = 1,
        LabelFormatter = v => TimeSpan.FromHours(v).ToString(@"hh\:mm"),
        Angle = -45
    };
    StyleAxis(axisX);
    plotModel.Axes.Add(axisX);

    // 12) Вісь Y: одна одиниця (сек/хв/год)
    var axisY = new LinearAxis
    {
        Position = AxisPosition.Left,
        Title = "Час",
        Minimum = 0,
        Maximum = maxY,
        MajorStep = majorY,
        MinorStep = minorY,
        LabelFormatter = value =>
        {
            // Тут 'value' вже у тій самій одиниці, що й ми обрали:
            if (unitLabel == "с")
            {
                int sec = (int)Math.Round(value);
                return $"{sec} с";
            }
            else if (unitLabel == "хв.")
            {
                int min = (int)Math.Round(value);
                return $"{min} хв.";
            }
            else // unitLabel == "год."
            {
                // Якщо значення ціле (наприклад 1.0) – показуємо без десяткових
                if (Math.Abs(value - Math.Round(value)) < 0.0001)
                {
                    return $"{(int)Math.Round(value)} год";
                }
                else
                {
                    // Якщо є дробова частина, показуємо одне число після коми:
                    return $"{value:0.#} год";
                }
            }
        }
    };
    StyleAxis(axisY);
    plotModel.Axes.Add(axisY);

    // 13) Додаємо вертикальні лінії‐сітки для кожної години
    for (int h = 0; h < 24; h++)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Vertical,
            X = h,
            Color = Form1.settings.ColorTheme == "dark"
                        ? OxyColor.FromRgb(81, 99, 119)
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = 1.5,
            LineStyle = LineStyle.Solid
        });
    }

    // 14) Горизонтальні лінії‐сітки від 0 до maxY з кроком minorY
    for (double y = 0; y <= maxY; y += minorY)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Horizontal,
            Y = y,
            Color = Form1.settings.ColorTheme == "dark"
                        ? OxyColor.FromRgb(81, 99, 119)
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = (Math.Abs(y % majorY) < 0.0001) ? 1.5 : 0.5,
            LineStyle = (Math.Abs(y % majorY) < 0.0001) ? LineStyle.Solid : LineStyle.Dot
        });
    }

    // 15) Налагоджуємо контролер, щоб тултіп показувався на ховер (mousemove), а не на клік
    var ctrl = plotView.Controller ?? plotView.ActualController;

    // Вимикаємо старе «LeftClick → нічого» (як ви й робили раніше)
    ctrl.UnbindMouseDown(
        OxyPlot.OxyMouseButton.Left,
        OxyPlot.OxyModifierKeys.None,
        clickCount: 1
    );

    // Додаємо новий «MouseMove (без кнопки) → HoverSnapTrack»
    ctrl.BindMouseMove(
        OxyPlot.OxyMouseButton.None,
        OxyPlot.OxyModifierKeys.None,
        PlotCommands.HoverSnapTrack
    );

    plotView.Controller = ctrl;

    // 16) Додаємо підпис “Усього витрачено” (як у вас було)
    this.label9 = CreateMainLabel("label9", "label9", 545, 550, new Size(530, 50));

    // 17) Відобразити PlotView на формі
    plotView.Model = plotModel;
    this.Controls.Add(plotView);
}

