private Panel viewport;
private Panel contentPanel;
private VScrollBar vScrollBar1;
// ... решта полів

private void BrowserInfo()
{
    bool isDarkTheme = Form1.settings.ColorTheme == "dark";

    // 1) Зовнішній viewport
    viewport = new Panel
    {
        Name = "viewport",
        Location = new Point(270, 100),
        Size = new Size(550, 400),
        BackColor = isDarkTheme
                     ? Color.FromArgb(2, 14, 25)
                     : Color.FromArgb(212, 220, 225),
        AutoScroll = false,              // Вимикаємо вбудований скрол
        BorderStyle = BorderStyle.FixedSingle
    };
    this.Controls.Add(viewport);

    // 2) Внутрішній contentPanel
    contentPanel = new Panel
    {
        Name = "contentPanel",
        Location = new Point(0, 0),
        AutoSize = true,
        AutoSizeMode = AutoSizeMode.GrowAndShrink
    };
    viewport.Controls.Add(contentPanel);

    // Щоб UpdateScrollBar() викликався, коли додаються/видаляються контролі:
    contentPanel.ControlAdded += (s, e) => UpdateScrollBar();
    contentPanel.ControlRemoved += (s, e) => UpdateScrollBar();

    // 3) Вертикальний скролбар
    vScrollBar1 = new VScrollBar
    {
        Minimum = 0,
        SmallChange = 20,
        Width = 16
    };
    vScrollBar1.Scroll += vScrollBar1_Scroll;
    this.Controls.Add(vScrollBar1);

    // 4) Підписуємося на зміну розміру форми, щоб підлаштовувати скролбар і viewport
    this.Resize += (s, e) => UpdateScrollBar();

    // 5) Щоб контрол реагував на колесо миші, коли курсор над viewport
    viewport.MouseWheel += Viewport_MouseWheel;
    viewport.MouseEnter += (s, e) => viewport.Focus();
    viewport.TabStop = true;

    // 6) Далі ваша ініціалізація даних
    InitBrowserInfo();
}

private void UpdateScrollBar()
{
    int contentHeight = contentPanel.Height;
    int viewHeight = viewport.ClientSize.Height;

    if (contentHeight > viewHeight)
    {
        // вмикаємо і налаштовуємо
        vScrollBar1.Enabled     = true;
        vScrollBar1.Visible     = true;
        vScrollBar1.Maximum     = contentHeight;
        vScrollBar1.LargeChange = viewHeight;
    }
    else
    {
        // контент вміщується без прокрутки
        vScrollBar1.Enabled = false;
        vScrollBar1.Visible = false;
        vScrollBar1.Value   = 0;
        contentPanel.Top    = 0;
    }

    // позиціонування скролбару поруч із viewport
    vScrollBar1.Location = new Point(viewport.Right, viewport.Top);
    vScrollBar1.Height   = viewport.Height;
}

