public class CustomScrollPanel : UserControl
{
    private Panel contentPanel;
    private VScrollBar vScroll;

    public CustomScrollPanel()
    {
        // –ó–æ–≤–Ω—ñ—à–Ω—ñ–π UserControl
        this.BackColor = Color.White;
        this.AutoSize = false;

        // –ü–∞–Ω–µ–ª—å –∑ –≤–º—ñ—Å—Ç–æ–º
        contentPanel = new Panel()
        {
            Location = new Point(0, 0),
            AutoSize = true,       // –±—É–¥–µ ¬´—Ä–æ—Å—Ç–∏—Å—å¬ª –ø—ñ–¥ Labels
            AutoSizeMode = AutoSizeMode.GrowAndShrink
        };

        // –í–µ—Ä—Ç–∏–∫–∞–ª—å–Ω–∏–π —Å–∫—Ä–æ–ª–ª–±–∞—Ä
        vScroll = new VScrollBar()
        {
            Dock = DockStyle.Right,
            Width = 16,            // –º–æ–∂–µ—Ç–µ –ø—ñ–¥–ª–∞—à—Ç—É–≤–∞—Ç–∏
            Minimum = 0,
        };
        vScroll.Scroll += VScroll_Scroll;

        // –î–æ–¥–∞—î–º–æ —É –∫–æ–Ω—Ç—Ä–æ–ª
        base.Controls.Add(contentPanel);
        base.Controls.Add(vScroll);

        // –ü—Ä–æ–±—Ä–æ—Å –∫–æ–Ω—Ç—Ä–æ–ª—ñ–≤ —Ç–∞–∫, —â–æ–± add(control) –∑–∞–Ω–æ—Å–∏–≤ –Ω–∞ contentPanel
        // (—â–æ–± –∑ Form–∞ –Ω–µ —Ç—Ä–µ–±–∞ –±—É–ª–æ —Ä–æ–±–∏—Ç–∏ contentPanel.Controls.Add())
    }

    // –ü–µ—Ä–µ–∫–∏–¥–∞—î–º–æ Controls -> contentPanel.Controls
    public override ControlCollection Controls => contentPanel.Controls;

    // –°–ª—É—Ö–∞—î–º–æ –ø–µ—Ä–µ–º—ñ—â–µ–Ω–Ω—è —Å–∫—Ä–æ–ª–ª–±–∞—Ä—É
    private void VScroll_Scroll(object sender, ScrollEventArgs e)
    {
        // ¬´–ü—ñ–¥–Ω—ñ–º–∞—î–º–æ¬ª –≤–º—ñ—Å—Ç –≤–≥–æ—Ä—É –Ω–∞ Value –ø—ñ–∫—Å–µ–ª—ñ–≤
        contentPanel.Top = -vScroll.Value;
    }

    // –û–±—Ä–æ–±–∫–∞ –∫–æ–ª–µ—Å–∞ –º–∏—à—ñ
    protected override void OnMouseWheel(MouseEventArgs e)
    {
        int newVal = vScroll.Value - e.Delta;
        newVal = Math.Max(vScroll.Minimum, Math.Min(newVal, vScroll.Maximum - vScroll.LargeChange + 1));
        vScroll.Value = newVal;
        base.OnMouseWheel(e);
    }

    // –ö–æ–ª–∏ —Ä–æ–∑–º—ñ—Ä –∑–º—ñ–Ω—é—î—Ç—å—Å—è ‚Äî –æ–Ω–æ–≤–ª—é—î–º–æ –ø–∞—Ä–∞–º–µ—Ç—Ä–∏ —Å–∫—Ä–æ–ª–ª–±–∞—Ä—É
    protected override void OnResize(EventArgs e)
    {
        base.OnResize(e);
        UpdateScrollBar();
    }

    private void UpdateScrollBar()
    {
        int contentHeight = contentPanel.Height;
        int viewHeight = this.ClientSize.Height;
        if (contentHeight > viewHeight)
        {
            vScroll.Enabled = true;
            vScroll.Maximum = contentHeight;
            vScroll.LargeChange = viewHeight;
            vScroll.SmallChange = 20;
            vScroll.Visible = true;
        }
        else
        {
            vScroll.Enabled = false;
            vScroll.Visible = false;
            vScroll.Value = 0;
            contentPanel.Top = 0;
        }
    }

    // –ü—ñ—Å–ª—è –¥–æ–¥–∞–≤–∞–Ω–Ω—è –Ω–æ–≤–∏—Ö –∫–æ–Ω—Ç—Ä–æ–ª—ñ–≤ ‚Äî —Ç—Ä–µ–±–∞ –æ–Ω–æ–≤–∏—Ç–∏ —Å–∫—Ä–æ–ª–ª–±–∞—Ä
    protected override void OnControlAdded(ControlEventArgs e)
    {
        base.OnControlAdded(e);
        UpdateScrollBar();
    }
}


