public class CustomVScrollBar : VScrollBar
{
    // –í–ª–∞—Å—Ç–∏–≤–æ—Å—Ç—ñ, —â–æ–± –ª–µ–≥–∫–æ –º—ñ–Ω—è—Ç–∏ –∫–æ–ª—å–æ—Ä–∏ –∑ —Ñ–æ—Ä–º–∏
    public Color TrackColor { get; set; } = Color.LightGray;
    public Color ThumbColor { get; set; } = Color.DarkGray;
    public int ThumbTransparency { get; set; } = 200; // 0‚Äì255

    public CustomVScrollBar()
    {
        // –î–æ–∑–≤–æ–ª—è—î–º–æ –≤–ª–∞—Å–Ω–∏–π —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ —ñ –ø–æ–¥–≤—ñ–π–Ω–∏–π –±—É—Ñ–µ—Ä
        this.SetStyle(
            ControlStyles.UserPaint
          | ControlStyles.AllPaintingInWmPaint
          | ControlStyles.OptimizedDoubleBuffer
          | ControlStyles.SupportsTransparentBackColor,
            true);

        this.BackColor = Color.Transparent;
    }

    protected override void OnPaint(PaintEventArgs e)
    {
        var g = e.Graphics;
        var r = this.ClientRectangle;

        // –ú–∞–ª—é—î–º–æ —Ñ–æ–Ω (—Ç—Ä–µ–∫)
        using (var brushTrack = new SolidBrush(TrackColor))
        {
            g.FillRectangle(brushTrack, r);
        }

        // –†–æ–∑—Ä–∞—Ö–æ–≤—É—î–º–æ –ø—Ä—è–º—É –ø–æ–ª–∑—É–Ω–∫–∞ (thumb)
        // –¢—É—Ç ‚Äì –¥—É–∂–µ —Å–ø—Ä–æ—â–µ–Ω–∞ —Ñ–æ—Ä–º—É–ª–∞: –≤–∏—Å–æ—Ç–∞ thumb = LargeChange
        int thumbHeight = this.LargeChange;
        int maxMovable = Math.Max(1, this.Maximum - this.LargeChange + 1);
        int thumbY = (int)((r.Height - thumbHeight) * (this.Value / (float)maxMovable));

        var thumbRect = new Rectangle(0, thumbY, r.Width, thumbHeight);

        // –ú–∞–ª—é—î–º–æ —Å–∞–º thumb —ñ–∑ –∑–∞–¥–∞–Ω–æ—é –ø—Ä–æ–∑–æ—Ä—ñ—Å—Ç—é
        var c = Color.FromArgb(ThumbTransparency, ThumbColor);
        using (var brushThumb = new SolidBrush(c))
        {
            g.FillRectangle(brushThumb, thumbRect);
        }
    }
