using System.Drawing;
using System.Windows.Forms;

public class CustomVScrollBar : VScrollBar
{
    // –ó—Ä—É—á–Ω—ñ –≤–ª–∞—Å—Ç–∏–≤–æ—Å—Ç—ñ –¥–ª—è –Ω–∞–ª–∞—à—Ç—É–≤–∞–Ω–Ω—è –∑ —Ñ–æ—Ä–º–∏:
    public Color TrackColor { get; set; } = Color.LightGray;
    public Color ThumbColor { get; set; } = Color.DodgerBlue;
    public int   ThumbAlpha { get; set; } = 200;    // 0 = –ø—Ä–æ–∑–æ—Ä–∏–π, 255 = –ø–æ–≤–Ω—ñ—Å—Ç—é –Ω–µ–ø—Ä–æ–∑–æ—Ä–∏–π

    public CustomVScrollBar()
    {
        // –î–æ–∑–≤–æ–ª—è—î–º–æ –≤–ª–∞—Å–Ω–µ –º–∞–ª—é–≤–∞–Ω–Ω—è —Ç–∞ –ø—Ä–æ–∑–æ—Ä–∏–π —Ñ–æ–Ω
        this.SetStyle(
            ControlStyles.UserPaint
          | ControlStyles.AllPaintingInWmPaint
          | ControlStyles.OptimizedDoubleBuffer
          | ControlStyles.SupportsTransparentBackColor,
            true);

        this.BackColor = Color.Transparent;
    }

    protected override void OnPaint(PaintEventArgs e)
    {
        var g = e.Graphics;
        var r = this.ClientRectangle;

        // 1) –ú–∞–ª—é—î–º–æ —Ç—Ä–µ–∫
        using (var brushTrack = new SolidBrush(TrackColor))
        {
            g.FillRectangle(brushTrack, r);
        }

        // 2) –û–±—á–∏—Å–ª—é—î–º–æ —Ä–æ–∑–º—ñ—Ä —ñ –ø–æ–∑–∏—Ü—ñ—é thumb
        int view   = this.LargeChange;               // –≤–∏—Å–æ—Ç–∞ ¬´–≤—ñ–∫–Ω–∞¬ª
        int total  = this.Maximum - this.Minimum + 1;
        int range  = Math.Max(1, total - view + 1);  // –Ω–∞—Å–∫—ñ–ª—å–∫–∏ –ø–æ–ª–∑—É–Ω–æ–∫ –º–æ–∂–µ –∑–º—ñ—â—É–≤–∞—Ç–∏—Å—å
        int thumbH = Math.Max(view, 20);             // –º—ñ–Ω—ñ–º–∞–ª—å–Ω–∞ –≤–∏—Å–æ—Ç–∞ 20px
        float perc = Value / (float)range;
        int   thumbY = (int)((r.Height - thumbH) * perc);

        var thumbRect = new Rectangle(0, thumbY, r.Width, thumbH);

        // 3) –ú–∞–ª—é—î–º–æ thumb –∑ –∞–ª—å—Ñ–∞-–∫–∞–Ω–∞–ª–æ–º
        var c = Color.FromArgb(ThumbAlpha, ThumbColor);
        using (var brushThumb = new SolidBrush(c))
        {
            g.FillRectangle(brushThumb, thumbRect);
        }
    }

    // –ü–æ—Ç—Ä—ñ–±–Ω–æ –ø–µ—Ä–µ–ª–∏—Ü—é–≤–∞—Ç–∏ –∫–æ–Ω—Ç—Ä–æ–ª –ø—Ä–∏ –∑–º—ñ–Ω—ñ –≤–ª–∞—Å—Ç–∏–≤–æ—Å—Ç–µ–π
    public void RefreshAppearance()
    {
        this.Invalidate();
    }
}

