private void buildDailyChart<T>(
    List<T> dataList,
    DateTime selectedDate,
    Func<T, (DateTime Start, DateTime Stop)?> convertToInterval)
{
    // 1) –ë–µ—Ä–µ–º–æ –ª–∏—à–µ –¥–∞—Ç—É –±–µ–∑ —á–∞—Å—É
    DateTime today = selectedDate.Date;

    // 2) –ü–∞—Ä—Å–∏–º–æ –≤—Å—ñ —ñ–Ω—Ç–µ—Ä–≤–∞–ª–∏ —Ä–æ–±–æ—Ç–∏/–∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—ñ
    var parsed = dataList
        .Select(convertToInterval)
        .Where(interval => interval.HasValue && interval.Value.Start != DateTime.MinValue)
        .Select(interval => interval.Value)
        .ToList();

    // 3) –†–∞—Ö—É—î–º–æ, —Å–∫—ñ–ª—å–∫–∏ —Ö–≤–∏–ª–∏–Ω –ø—Ä–∏–ø–∞–¥–∞—î –Ω–∞ –∫–æ–∂–Ω—É –≥–æ–¥–∏–Ω—É –æ–±—Ä–∞–Ω–æ–≥–æ –¥–Ω—è
    double[] minutesPerHour = new double[24];
    foreach (var interval in parsed)
    {
        DateTime start = interval.Start;
        DateTime stop = interval.Stop;

        // –Ü–≥–Ω–æ—Ä—É—î–º–æ —ñ–Ω—Ç–µ—Ä–≤–∞–ª–∏, —è–∫—ñ –≤–∑–∞–≥–∞–ª—ñ –Ω–µ –Ω–∞–ª–µ–∂–∞—Ç—å –¥–æ —Å—å–æ–≥–æ–¥–Ω—ñ—à–Ω—å–æ—ó –¥–∞—Ç–∏
        if (stop < today || start >= today.AddDays(1))
            continue;

        // –û–±—Ä—ñ–∑–∞—î–º–æ —ñ–Ω—Ç–µ—Ä–≤–∞–ª –¥–æ –º–µ–∂ –ø–æ—Ç–æ—á–Ω–æ–≥–æ –¥–Ω—è
        if (start < today) 
            start = today;
        if (stop > today.AddDays(1)) 
            stop = today.AddDays(1);

        // –†–æ–∑–±–∏–≤–∞—î–º–æ –¥–æ–≤–≥–∏–π —ñ–Ω—Ç–µ—Ä–≤–∞–ª –Ω–∞ —à–º–∞—Ç–∫–∏ –ø–æ –≥–æ–¥–∏–Ω–∞—Ö
        while (start < stop)
        {
            int hour = start.Hour;
            DateTime nextHour = new DateTime(
                start.Year, start.Month, start.Day, hour, 0, 0
            ).AddHours(1);
            DateTime segmentEnd = (stop < nextHour) ? stop : nextHour;

            double minutes = (segmentEnd - start).TotalMinutes;
            minutesPerHour[hour] += minutes;

            start = segmentEnd;
        }
    }

    // 4) –í–∏–∑–Ω–∞—á–∞—î–º–æ, —É —è–∫—ñ–π –æ–¥–∏–Ω–∏—Ü—ñ –±—É–¥–µ–º–æ –ø–æ–∫–∞–∑—É–≤–∞—Ç–∏ –≤—ñ—Å—å Y: —Å–µ–∫—É–Ω–¥–∏, —Ö–≤–∏–ª–∏–Ω–∏ —á–∏ –≥–æ–¥–∏–Ω–∏
    double rawMax = minutesPerHour.Max(); // –º–∞–∫—Å. –∑–Ω–∞—á–µ–Ω–Ω—è –≤ —Ö–≤–∏–ª–∏–Ω–∞—Ö

    double scaleFactor;
    string unitLabel;

    if (rawMax < 1.0)
    {
        // –Ø–∫—â–æ –º–∞–∫—Å–∏–º—É–º < 1 —Ö–≤–∏–ª–∏–Ω–∏ ‚Üí –ø–µ—Ä–µ–≤–æ–¥–∏–º–æ –≤—Å–µ –≤ —Å–µ–∫—É–Ω–¥–∏
        scaleFactor = 60.0;  // —Ö–≤–∏–ª–∏–Ω–∏ * 60 = —Å–µ–∫—É–Ω–¥–∏
        unitLabel = "—Å";
    }
    else if (rawMax < 60.0)
    {
        // –Ø–∫—â–æ –º–∞–∫—Å–∏–º—É–º < 60 —Ö–≤–∏–ª–∏–Ω ‚Üí –∑–∞–ª–∏—à–∞—î–º–æ –≤ —Ö–≤–∏–ª–∏–Ω–∞—Ö
        scaleFactor = 1.0;   // –ø—Ä–æ—Å—Ç–æ —Ö–≤–∏–ª–∏–Ω–∏
        unitLabel = "—Ö–≤.";
    }
    else
    {
        // –Ø–∫—â–æ –º–∞–∫—Å–∏–º—É–º ‚â• 60 —Ö–≤–∏–ª–∏–Ω ‚Üí –ø–µ—Ä–µ–≤–æ–¥–∏–º–æ –≤—Å–µ –≤ –≥–æ–¥–∏–Ω–∏
        scaleFactor = 1.0 / 60.0; // —Ö–≤–∏–ª–∏–Ω–∏ / 60 = –≥–æ–¥–∏–Ω–∏
        unitLabel = "–≥–æ–¥.";
    }

    // 5) –û–±—á–∏—Å–ª—é—î–º–æ –º–∞–∫—Å–∏–º–∞–ª—å–Ω–µ –∑–Ω–∞—á–µ–Ω–Ω—è –≤ –æ–±—Ä–∞–Ω—ñ–π –æ–¥–∏–Ω–∏—Ü—ñ
    double maxInUnit = rawMax * scaleFactor;
    double maxY = Math.Ceiling(maxInUnit);
    if (maxY <= 0)
        maxY = 1;

    // 6) –í–∏–∑–Ω–∞—á–∞—î–º–æ –∫—Ä–æ–∫–∏ Major/Minor –¥–ª—è –æ—Å—ñ Y
    double majorY = Math.Max(1.0, Math.Ceiling(maxY / 10.0));
    double minorY = majorY / 2.0;

    // 7) –°—Ç–≤–æ—Ä—é—î–º–æ PlotView —Ç–∞ –æ—Ç—Ä–∏–º—É—î–º–æ PlotModel
    var plotView = CreatePlotView();
    var plotModel = plotView.Model;

    // 8) –ù–∞–ª–∞—à—Ç–æ–≤—É—î–º–æ —Å–µ—Ä—ñ—é
    var series = new LineSeries
    {
        MarkerType = MarkerType.None,
        Color = Form1.settings.ColorTheme == "dark"
                    ? OxyColor.FromRgb(159, 183, 213)
                    : OxyColor.FromRgb(82, 82, 82),
        StrokeThickness = 2
    };

    // 9) –î–æ–¥–∞—î–º–æ —Ç–æ—á–∫–∏: X = –≥–æ–¥–∏–Ω–∞, Y = minutesPerHour[h] * scaleFactor
    for (int h = 0; h < 24; h++)
    {
        double yValue = minutesPerHour[h] * scaleFactor;
        series.Points.Add(new DataPoint(h, yValue));
    }

    // 10) –î–æ–¥–∞—î–º–æ TrackerFormatString, —â–æ–± –ø—Ä–∏ –Ω–∞–≤–µ–¥–µ–Ω–Ω—ñ –ø–æ–∫–∞–∑—É–≤–∞–ª–æ—Å—è –∑–Ω–∞—á–µ–Ω–Ω—è
    series.TrackerFormatString = $"–ì–æ–¥–∏–Ω–∞: {{2:0}}\n–ß–∞—Å: {{4}}";

    plotModel.Series.Add(series);

    // 11) –í—ñ—Å—å X (–∑–∞–ª–∏—à–∞—î–º–æ —è–∫ –±—É–ª–æ)
    var axisX = new LinearAxis
    {
        Position = AxisPosition.Bottom,
        Title = "–ì–æ–¥–∏–Ω–∞",
        Minimum = -0.5,
        Maximum = 23.5,
        MajorStep = 1,
        MinorStep = 1,
        LabelFormatter = v => TimeSpan.FromHours(v).ToString(@"hh\:mm"),
        Angle = -45
    };
    StyleAxis(axisX);
    plotModel.Axes.Add(axisX);

    // 12) –í—ñ—Å—å Y: –æ–¥–Ω–∞ –æ–¥–∏–Ω–∏—Ü—è (—Å–µ–∫/—Ö–≤/–≥–æ–¥)
    var axisY = new LinearAxis
    {
        Position = AxisPosition.Left,
        Title = "–ß–∞—Å",
        Minimum = 0,
        Maximum = maxY,
        MajorStep = majorY,
        MinorStep = minorY,
        LabelFormatter = value =>
        {
            // –¢—É—Ç 'value' –≤–∂–µ —É —Ç—ñ–π —Å–∞–º—ñ–π –æ–¥–∏–Ω–∏—Ü—ñ, —â–æ –π –º–∏ –æ–±—Ä–∞–ª–∏:
            if (unitLabel == "—Å")
            {
                int sec = (int)Math.Round(value);
                return $"{sec} —Å";
            }
            else if (unitLabel == "—Ö–≤.")
            {
                int min = (int)Math.Round(value);
                return $"{min} —Ö–≤.";
            }
            else // unitLabel == "–≥–æ–¥."
            {
                // –Ø–∫—â–æ –∑–Ω–∞—á–µ–Ω–Ω—è —Ü—ñ–ª–µ (–Ω–∞–ø—Ä–∏–∫–ª–∞–¥ 1.0) ‚Äì –ø–æ–∫–∞–∑—É—î–º–æ –±–µ–∑ –¥–µ—Å—è—Ç–∫–æ–≤–∏—Ö
                if (Math.Abs(value - Math.Round(value)) < 0.0001)
                {
                    return $"{(int)Math.Round(value)} –≥–æ–¥";
                }
                else
                {
                    // –Ø–∫—â–æ —î –¥—Ä–æ–±–æ–≤–∞ —á–∞—Å—Ç–∏–Ω–∞, –ø–æ–∫–∞–∑—É—î–º–æ –æ–¥–Ω–µ —á–∏—Å–ª–æ –ø—ñ—Å–ª—è –∫–æ–º–∏:
                    return $"{value:0.#} –≥–æ–¥";
                }
            }
        }
    };
    StyleAxis(axisY);
    plotModel.Axes.Add(axisY);

    // 13) –î–æ–¥–∞—î–º–æ –≤–µ—Ä—Ç–∏–∫–∞–ª—å–Ω—ñ –ª—ñ–Ω—ñ—ó‚Äê—Å—ñ—Ç–∫–∏ –¥–ª—è –∫–æ–∂–Ω–æ—ó –≥–æ–¥–∏–Ω–∏
    for (int h = 0; h < 24; h++)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Vertical,
            X = h,
            Color = Form1.settings.ColorTheme == "dark"
                        ? OxyColor.FromRgb(81, 99, 119)
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = 1.5,
            LineStyle = LineStyle.Solid
        });
    }

    // 14) –ì–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª—å–Ω—ñ –ª—ñ–Ω—ñ—ó‚Äê—Å—ñ—Ç–∫–∏ –≤—ñ–¥ 0 –¥–æ maxY –∑ –∫—Ä–æ–∫–æ–º minorY
    for (double y = 0; y <= maxY; y += minorY)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Horizontal,
            Y = y,
            Color = Form1.settings.ColorTheme == "dark"
                        ? OxyColor.FromRgb(81, 99, 119)
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = (Math.Abs(y % majorY) < 0.0001) ? 1.5 : 0.5,
            LineStyle = (Math.Abs(y % majorY) < 0.0001) ? LineStyle.Solid : LineStyle.Dot
        });
    }

    // 15) –ù–∞–ª–∞–≥–æ–¥–∂—É—î–º–æ –∫–æ–Ω—Ç—Ä–æ–ª–µ—Ä, —â–æ–± —Ç—É–ª—Ç—ñ–ø –ø–æ–∫–∞–∑—É–≤–∞–≤—Å—è –Ω–∞ —Ö–æ–≤–µ—Ä (mousemove), –∞ –Ω–µ –Ω–∞ –∫–ª—ñ–∫
    var ctrl = plotView.Controller ?? plotView.ActualController;

    // –í–∏–º–∏–∫–∞—î–º–æ —Å—Ç–∞—Ä–µ ¬´LeftClick ‚Üí –Ω—ñ—á–æ–≥–æ¬ª (—è–∫ –≤–∏ –π —Ä–æ–±–∏–ª–∏ —Ä–∞–Ω—ñ—à–µ)
    ctrl.UnbindMouseDown(
