private void PlotView_MouseDown(object sender, MouseEventArgs e)
{
    if (e.Button != MouseButtons.Left) return;
    if (!(sender is PlotView view)) return;
    var model = view.Model;
    if (model == null) return;

    // –•–æ–≤–∞—î–º–æ —Å—Ç–∞—Ä–∏–π —Ç—É–ª—Ç—ñ–ø (—è–∫—â–æ –±—É–≤):
    _customToolTip.Hide(view);

    // –ë–µ—Ä–µ–º–æ –ø–µ—Ä—à—É LineSeries (—è–∫ —É –≤–∞—Å –±—É–ª–æ)
    var series = model.Series.OfType<LineSeries>().FirstOrDefault();
    if (series == null) return;

    // –ó–Ω–∞—Ö–æ–¥–∏–º–æ –Ω–∞–π–±–ª–∏–∂—á—É —Ç–æ—á–∫—É –¥–æ –∫–ª—ñ–∫—É
    var sp = new ScreenPoint(e.X, e.Y);
    var nearest = series.GetNearestPoint(sp, interpolate: false);
    if (nearest == null) return;

    // –Ø–∫—â–æ –¥–∏—Å—Ç–∞–Ω—Ü—ñ—è –¥–æ —Ç–æ—á–∫–∏ > 10 –ø—ñ–∫—Å–µ–ª—ñ–≤, –ø—Ä–∏–ø–∏–Ω—è—î–º–æ
    if (nearest.Position.DistanceTo(sp) >= 10) return;

    var dp = nearest.DataPoint;

    // –ö–æ–Ω–≤–µ—Ä—Ç—É—î–º–æ X-–ø–æ–∑–∏—Ü—ñ—é –Ω–∞–∑–∞–¥ —É DateTime (—è–∫ —É –≤–∞—Å –±—É–ª–æ)
    var dt = DateTimeAxis.ToDateTime(dp.X, TimeSpan.FromSeconds(1));

    // dp.Y ‚Äî —Ü–µ –∑–Ω–∞—á–µ–Ω–Ω—è —É –º–∞—Å—à—Ç–∞–±—ñ _currentScaleFactor
    // –ü–µ—Ä–µ–¥–∞—î–º–æ —Å–∞–º–µ thisY = dp.Y —É –º–µ—Ç–æ–¥ FormatTooltipValue (–≤—Å–µ—Ä–µ–¥–∏–Ω—ñ GetToolTipText)
    string text = GetToolTipText(dt, dp.Y, _showDateInTooltip);

    // –ü–æ–∫–∞–∑—É—î–º–æ –Ω–∞—à –∫–∞—Å—Ç–æ–º–Ω–∏–π —Ç—É–ª—Ç—ñ–ø –±—ñ–ª—è –º–∏—à—ñ
    _customToolTip.Show(text, view, e.X + 10, e.Y + 10);
}


