        scaleFactor = 1.0;   // –ø—Ä–æ—Å—Ç–æ —Ö–≤–∏–ª–∏–Ω–∏
        unitLabel = "—Ö–≤.";
    }
    else
    {
        // –Ø–∫—â–æ –º–∞–∫—Å–∏–º—É–º ‚â• 60 —Ö–≤–∏–ª–∏–Ω ‚Üí –ø–µ—Ä–µ–≤–æ–¥–∏–º–æ –≤—Å–µ –≤ –≥–æ–¥–∏–Ω–∏
        scaleFactor = 1.0 / 60.0; // —Ö–≤–∏–ª–∏–Ω–∏ / 60 = –≥–æ–¥–∏–Ω–∏
