–ö–æ–ø–∏—Ä–æ–≤–∞—Ç—å
–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
private void buildDailyChart<T>(List<T> dataList, DateTime selectedDate, Func<T, (DateTime Start, DateTime Stop)?> convertToInterval)
{
    DateTime today = selectedDate.Date;

    // ================================
    // 1. –ü–∞—Ä—Å–∏–º–æ –≤—Å—ñ —ñ–Ω—Ç–µ—Ä–≤–∞–ª–∏, —è–∫ —É –≤–∞—Å –±—É–ª–æ
    var parsed = dataList
        .Select(convertToInterval)
        .Where(interval => interval.HasValue && interval.Value.Start != DateTime.MinValue)
        .Select(interval => interval.Value)
        .ToList();

    // ================================
    // 2. –†–∞—Ö—É—î–º–æ —Ö–≤–∏–ª–∏–Ω–∏ –∑–∞ –∫–æ–∂–Ω—É –≥–æ–¥–∏–Ω—É
    double[] minutesPerHour = new double[24];
    foreach (var interval in parsed)
    {
        DateTime start = interval.Start;
        DateTime stop = interval.Stop;

        if (stop < today || start >= today.AddDays(1))
            continue;

        if (start < today) start = today;
        if (stop > today.AddDays(1)) stop = today.AddDays(1);

        while (start < stop)
        {
            int hour = start.Hour;
            DateTime nextHour = new DateTime(start.Year, start.Month, start.Day, hour, 0, 0).AddHours(1);
            DateTime segmentEnd = stop < nextHour ? stop : nextHour;

            double minutes = (segmentEnd - start).TotalMinutes;
            minutesPerHour[hour] += minutes;

            start = segmentEnd;
        }
    }

    // ================================
    // 3. –í–∏–∑–Ω–∞—á–∞—î–º–æ ‚Äú–º–∞—Å—à—Ç–∞–±‚Äù —Ç–∞ –Ω–∞–∑–≤—É –æ–¥–∏–Ω–∏—Ü—ñ –∑–∞–ª–µ–∂–Ω–æ –≤—ñ–¥ max(minutesPerHour)
    double rawMax = minutesPerHour.Max(); // –Ω–∞–π–±—ñ–ª—å—à–∞ –∫—ñ–ª—å–∫—ñ—Å—Ç—å —Ö–≤–∏–ª–∏–Ω
    double scaleFactor;
    string unitLabel;

    if (rawMax < 1.0)
    {
        // —è–∫—â–æ –º–µ–Ω—à–µ 1 —Ö–≤ ‚Üí –ø–æ–∫–∞–∑—É—î–º–æ –≤ —Å–µ–∫—É–Ω–¥–∞—Ö
        scaleFactor = 60.0;    // –∑ —Ö–≤–∏–ª–∏–Ω ‚Üí —É —Å–µ–∫—É–Ω–¥–∏
        unitLabel = "—Å";
    }
    else if (rawMax < 60.0)
    {
        // —è–∫—â–æ –º–µ–Ω—à–µ 60 —Ö–≤ ‚Üí –ø–æ–∫–∞–∑—É—î–º–æ –≤ —Ö–≤–∏–ª–∏–Ω–∞—Ö
        scaleFactor = 1.0;
        unitLabel = "—Ö–≤.";
    }
    else
    {
        // —è–∫—â–æ >= 60 —Ö–≤ (—Ç–æ–±—Ç–æ 1 –≥–æ–¥.) ‚Üí –ø–µ—Ä–µ–≤–æ–¥–∏–º–æ –≤ –≥–æ–¥–∏–Ω–∏
        scaleFactor = 1.0 / 60.0;  // –∑ —Ö–≤–∏–ª–∏–Ω ‚Üí —É –≥–æ–¥–∏–Ω–∏
        unitLabel = "–≥–æ–¥.";
    }

    // 4. –†–æ–∑—Ä–∞—Ö–æ–≤—É—î–º–æ –º–∞–∫—Å–∏–º—É–º–∏ —Ç–∞ –∫—Ä–æ–∫–∏ —É–∂–µ ‚Äú–≤ –Ω–æ–≤—ñ–π –æ–¥–∏–Ω–∏—Ü—ñ‚Äù
    double maxInChosenUnit = rawMax * scaleFactor; 
    double maxY = Math.Ceiling(maxInChosenUnit + 1.0);
    double majorY = Math.Max(1.0, Math.Ceiling(maxY / 10.0));
    double minorY = majorY / 2.0;

    // ================================
    // 5. –°—Ç–≤–æ—Ä—é—î–º–æ OxyPlot.Series —ñ —Ç–æ—á–∫–∏, –∞–ª–µ –∑ –º–∞—Å—à—Ç–∞–±—É–≤–∞–Ω–Ω—è–º
    var series = new LineSeries
    {
        MarkerType = MarkerType.None,
        Color = Form1.settings.ColorTheme == "dark" 
                    ? OxyColor.FromRgb(159, 183, 213) 
                    : OxyColor.FromRgb(82, 82, 82),
        StrokeThickness = 2
    };

    for (int h = 0; h < 24; h++)
    {
        // –∑–∞–º—ñ—Å—Ç—å (h, minutesPerHour[h]) –¥–æ–¥–∞—î–º–æ (h, minutesPerHour[h] * scaleFactor)
        double yValue = minutesPerHour[h] * scaleFactor;
        series.Points.Add(new DataPoint(h, yValue));
    }

    // ================================
    // 6. –ü–æ–±—É–¥–æ–≤–∞ –æ—Å—ñ X (–∑–∞–ª–∏—à–∞—î–º–æ —è–∫ –±—É–ª–æ)
    var axisX = new LinearAxis
    {
        Position = AxisPosition.Bottom,
        Title = "–ì–æ–¥–∏–Ω–∞",
        Minimum = -0.5,
        Maximum = 23.5,
        MajorStep = 1,
        MinorStep = 1,
        LabelFormatter = v => TimeSpan.FromHours(v).ToString(@"hh\:mm"),
        Angle = -45
    };
    StyleAxis(axisX);

    // ================================
    // 7. –ü–æ–±—É–¥–æ–≤–∞ –æ—Å—ñ Y —ñ–∑ —î–¥–∏–Ω–æ—é (‚Äú—á–∏—Å—Ç–æ—é‚Äù) –æ–¥–∏–Ω–∏—Ü–µ—é
    var axisY = new LinearAxis
    {
        Position = AxisPosition.Left,
        Title = "–ß–∞—Å",
        Minimum = 0,
        Maximum = maxY,
        MajorStep = majorY,
        MinorStep = minorY,
        LabelFormatter = value =>
        {
            // value —Ç—É—Ç –≤–∂–µ —É —Ç—ñ–π —Å–∞–º—ñ–π –æ–¥–∏–Ω–∏—Ü—ñ, —â–æ –π yValue:
            //   ‚Äì —è–∫—â–æ seconds: value = –∫—ñ–ª—å–∫—ñ—Å—Ç—å —Å–µ–∫—É–Ω–¥
            //   ‚Äì —è–∫—â–æ minutes:  value = –∫—ñ–ª—å–∫—ñ—Å—Ç—å —Ö–≤–∏–ª–∏–Ω
            //   ‚Äì —è–∫—â–æ hours:    value = –∫—ñ–ª—å–∫—ñ—Å—Ç—å –≥–æ–¥–∏–Ω (–¥—Ä–æ–±–æ–≤–µ)
            // –û–±–µ—Ä–µ–º–æ —Ñ–æ—Ä–º–∞—Ç –≤–∏–≤–æ–¥—É:
            if (unitLabel == "—Å")
            {
                // —Å–µ–∫—É–Ω–¥–∏ –∑–∞–æ–∫—Ä—É–≥–ª—é—î–º–æ –¥–æ int
                int seconds = (int)Math.Round(value);
                return $"{seconds} —Å";
            }
            else if (unitLabel == "—Ö–≤.")
            {
                int minutes = (int)Math.Round(value);
                return $"{minutes} —Ö–≤.";
            }
            else // unitLabel == "–≥–æ–¥."
            {
                // value –≤–∂–µ –¥—ñ–ª—å–Ω–µ –Ω–∞ 60 (—Ç–æ–±—Ç–æ –≤ –≥–æ–¥–∏–Ω–∞—Ö). 
                // –§–æ—Ä–º–∞—Ç—É–≤–∞—Ç–∏–º–µ–º–æ –∑ –¥–≤–æ–º–∞ –∑–Ω–∞–∫–∞–º–∏ –ø—ñ—Å–ª—è –∫–æ–º–∏, 
                // –Ω–∞–ø—Ä–∏–∫–ª–∞–¥ "1.25 –≥–æ–¥." (1 –≥–æ–¥–∏–Ω–∞ 15 —Ö–≤).
                return $"{value:F2} –≥–æ–¥.";
            }
        }
    };
    StyleAxis(axisY);

    // –î–æ–¥–∞—î–º–æ –≤–µ—Ä—Ç–∏–∫–∞–ª—å–Ω—ñ —Ç–∞ –≥–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª—å–Ω—ñ –ª—ñ–Ω—ñ—ó (–∑–∞ –≤–∞—à–∏–º –∫–æ–¥–æ–º)
    // ‚Ä¶ —Ç–µ —Å–∞–º–µ, —â–æ —É –≤–∞—Å –±—É–ª–æ —Ä–∞–Ω—ñ—à–µ, –ª–∏—à –∑–∞–º—ñ–Ω–∏—Ç–∏ maxY, majorY, minorY, axisY ‚Ä¶
    // –ù–∞–ø—Ä–∏–∫–ª–∞–¥:
    for (int h = 0; h < 24; h++)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Vertical,
            X = h,
            Color = Form1.settings.ColorTheme == "dark" 
                        ? OxyColor.FromRgb(81, 99, 119) 
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = 1.5,
            LineStyle = LineStyle.Solid
        });
    }

    for (double y = 0; y <= maxY; y += minorY)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Horizontal,
            Y = y,
            Color = Form1.settings.ColorTheme == "dark" 
                        ? OxyColor.FromRgb(81, 99, 119) 
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = (y % majorY == 0) ? 1.5 : 0.5,
            LineStyle = (y % majorY == 0) ? LineStyle.Solid : LineStyle.Dot
        });
    }

    // ================================
    // 8. –í—Å—Ç–∞–≤–ª—è—î–º–æ –≤—Å–µ –≤ PlotModel —ñ –≤–∏–≤–æ–¥–∏–º–æ –Ω–∞ —Ñ–æ—Ä–º—É
    var plotView = CreatePlotView();
    var plotModel = plotView.Model;

    plotModel.Series.Add(series);
    plotModel.Axes.Add(axisX);
    plotModel.Axes.Add(axisY);

    // –Ø–∫—â–æ —Ö–æ—á–µ—Ç–µ —Ç–∞–∫–æ–∂ –æ–Ω–æ–≤–∏—Ç–∏ ‚Äútooltip‚Äù (tracker), 
    // —â–æ–± —É –ø—ñ–¥–∫–∞–∑—Ü—ñ —Ç–µ–∂ –±—É–ª–∞ –æ–¥–Ω–∞–∫–æ–≤–µ unitLabel,
    // –º–æ–∂–Ω–∞ –ø—Ä–∏ –ø–æ—Ç—Ä–µ–±—ñ –∑–∞–¥–∞—Ç–∏ series.TrackerFormatString –∞–Ω–∞–ª–æ–≥—ñ—á–Ω–æ:
    // series.TrackerFormatString = $"–ß–∞—Å: {{2:0.##}} {unitLabel}";

    plotView.MouseDown += PlotView_MouseDown;
    plotView.MouseUp += PlotView_MouseUp;

    this.label9 = CreateMainLabel("label9", "label9", 545, 550, new Size(530, 50));
    plotView.Model = plotModel;
    this.Controls.Add(plotView);
}

