private void buildDailyChart<T>(
    List<T> dataList,
    DateTime selectedDate,
    Func<T, (DateTime Start, DateTime Stop)?> convertToInterval)
{
    // 1) –õ–∏—à–µ –¥–∞—Ç–∞ –±–µ–∑ —á–∞—Å—É
    DateTime today = selectedDate.Date;

    // 2) –†–æ–∑–ø–∞—Ä—Å–∏–º–æ —ñ–Ω—Ç–µ—Ä–≤–∞–ª–∏ Start/Stop
    var parsed = dataList
        .Select(convertToInterval)
        .Where(interval => interval.HasValue && interval.Value.Start != DateTime.MinValue)
        .Select(interval => interval.Value)
        .ToList();

    // 3) –†–∞—Ö—É—î–º–æ —Ä—ñ–≤–µ–Ω—å –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—ñ (—É —Ö–≤–∏–ª–∏–Ω–∞—Ö) –ø–æ –≥–æ–¥–∏–Ω–∞—Ö
    double[] minutesPerHour = new double[24];
    foreach (var interval in parsed)
    {
        DateTime start = interval.Start;
        DateTime stop = interval.Stop;

        // –Ø–∫—â–æ —ñ–Ω—Ç–µ—Ä–≤–∞–ª –ø–æ–≤–Ω—ñ—Å—Ç—é –ø–æ–∑–∞ —Å—å–æ–≥–æ–¥–Ω—ñ—à–Ω—ñ–º –¥–Ω–µ–º ‚Äî –ø—Ä–æ–ø—É—Å–∫–∞—î–º–æ
        if (stop < today || start >= today.AddDays(1))
            continue;

        // –û–±—Ä—ñ–∑–∞—î–º–æ —ñ–Ω—Ç–µ—Ä–≤–∞–ª –¥–æ –º–µ–∂ –ø–æ—Ç–æ—á–Ω–æ–≥–æ –¥–Ω—è
        if (start < today)
            start = today;
        if (stop > today.AddDays(1))
            stop = today.AddDays(1);

        // –†–æ–∑–±–∏–≤–∞—î–º–æ —ñ–Ω—Ç–µ—Ä–≤–∞–ª –ø–æ–º–æ–¥—É–ª—å–Ω–æ –ø–æ –≥–æ–¥–∏–Ω–∞—Ö
        while (start < stop)
        {
            int hour = start.Hour;
            DateTime nextHour = new DateTime(start.Year, start.Month, start.Day, hour, 0, 0).AddHours(1);
            DateTime segmentEnd = (stop < nextHour) ? stop : nextHour;

            double minutes = (segmentEnd - start).TotalMinutes;
            minutesPerHour[hour] += minutes;

            start = segmentEnd;
        }
    }

    // 4) –í–∏–∑–Ω–∞—á–∞—î–º–æ ‚Äú–º–∞—Å—à—Ç–∞–±‚Äù —ñ ‚Äú—î–¥–∏–Ω—É –æ–¥–∏–Ω–∏—Ü—é‚Äù (—Å–µ–∫/—Ö–≤/–≥–æ–¥)
    double rawMax = minutesPerHour.Max(); // –º–∞–∫—Å–∏–º—É–º —É —Ö–≤–∏–ª–∏–Ω–∞—Ö

    double scaleFactor;
    string unitLabel;

    if (rawMax < 1.0)
    {
        // –Ø–∫—â–æ <1 —Ö–≤–∏–ª–∏–Ω–∏, –ø–æ–∫–∞–∑—É—î–º–æ –≤ —Å–µ–∫—É–Ω–¥–∞—Ö
        scaleFactor = 60.0;  // —Ö–≤ ‚Üí —Å–µ–∫
        unitLabel = "—Å";
    }
    else if (rawMax < 60.0)
    {
        // –Ø–∫—â–æ <60 —Ö–≤, –∑–∞–ª–∏—à–∞—î–º–æ –≤ —Ö–≤–∏–ª–∏–Ω–∞—Ö
        scaleFactor = 1.0;   // –≤–∂–µ –≤ —Ö–≤–∏–ª–∏–Ω–∞—Ö
        unitLabel = "—Ö–≤.";
    }
    else
    {
        // –Ø–∫—â–æ ‚â•60 —Ö–≤, –ø–µ—Ä–µ–≤–æ–¥–∏–º–æ –≤ –≥–æ–¥–∏–Ω–∏
        scaleFactor = 1.0 / 60.0; // —Ö–≤ ‚Üí –≥–æ–¥
        unitLabel = "–≥–æ–¥.";
    }

    // 5) –†–æ–∑—Ä–∞—Ö–æ–≤—É—î–º–æ –º–∞–∫—Å–∏–º—É–º —É –≤–∏–±—Ä–∞–Ω—ñ–π –æ–¥–∏–Ω–∏—Ü—ñ
    double maxInUnit = rawMax * scaleFactor;
    // –ó–Ω—ñ–º–∞—î–º–æ –∑–∞–π–≤–µ +1, —â–æ–± –≤–∏—â–∏–π —Ç–∏–∫ —Ç–æ—á–Ω–æ –≤—ñ–¥–ø–æ–≤—ñ–¥–∞–≤ —Ñ–∞–∫—Ç–∏—á–Ω–æ–º—É –º–∞–∫—Å–∏–º—É–º—É (–∞–±–æ —Ç—Ä–æ—Ö–∏ –±—ñ–ª—å—à–µ)
    double maxY = Math.Ceiling(maxInUnit);
    // –Ø–∫—â–æ –≤–∏–π—à–ª–æ maxY == 0 (–Ω–∞–ø—Ä–∏–∫–ª–∞–¥, —É—Å—è –∞–∫—Ç–∏–≤–Ω—ñ—Å—Ç—å < 1 –æ–¥–∏–Ω–∏—Ü—ñ), –≤—Å–µ –æ–¥–Ω–æ —Å—Ç–∞–≤–∏–º–æ —Ö–æ—á–∞ –± 1
    if (maxY <= 0)
        maxY = 1;

    // 6) –û–±—á–∏—Å–ª—é—î–º–æ MajorStep/MinorStep –≤–∂–µ –ø—ñ–¥ –Ω–æ–≤–∏–π –¥—ñ–∞–ø–∞–∑–æ–Ω
    double majorY = Math.Max(1.0, Math.Ceiling(maxY / 10.0));
    double minorY = majorY / 2.0;

    // 7) –°—Ç–≤–æ—Ä—é—î–º–æ PlotView —ñ PlotModel
    var plotView = CreatePlotView();
    var plotModel = plotView.Model;

    // 8) –°–µ—Ä—ñ—è –∑ —Ç–æ—á–∫–∞–º–∏ (X = –≥–æ–¥–∏–Ω–∞, Y = value * scaleFactor)
    var series = new LineSeries
    {
        MarkerType = MarkerType.None,
        Color = Form1.settings.ColorTheme == "dark"
                    ? OxyColor.FromRgb(159, 183, 213)
                    : OxyColor.FromRgb(82, 82, 82),
        StrokeThickness = 2
    };

    // –í–∏–º–∏–∫–∞—î–º–æ –∑–≤–∏—á–∞–π–Ω–∏–π –∫–ª—ñ–∫ (—â–æ–± –Ω–µ zoom/pan)
    var ctrl = plotView.Controller ?? plotView.ActualController;
    ctrl.UnbindMouseDown(
        OxyPlot.OxyMouseButton.Left,
        OxyPlot.OxyModifierKeys.None,
        clickCount: 1
    );
    plotView.Controller = ctrl;

    // –î–æ–¥–∞—î–º–æ —Ç–æ—á–∫–∏
    for (int h = 0; h < 24; h++)
    {
        double yValue = minutesPerHour[h] * scaleFactor;
        series.Points.Add(new DataPoint(h, yValue));
    }
    plotModel.Series.Add(series);

    // 9) –í—ñ—Å—å X (—è–∫ –±—É–ª–æ —Ä–∞–Ω—ñ—à–µ)
    var axisX = new LinearAxis
    {
        Position = AxisPosition.Bottom,
        Title = "–ì–æ–¥–∏–Ω–∞",
        Minimum = -0.5,
        Maximum = 23.5,
        MajorStep = 1,
        MinorStep = 1,
        LabelFormatter = v => TimeSpan.FromHours(v).ToString(@"hh\:mm"),
        Angle = -45
    };
    StyleAxis(axisX);
    plotModel.Axes.Add(axisX);

    // 10) –í—ñ—Å—å Y (–ª–∏—à–µ –æ–¥–Ω–∞ –æ–¥–∏–Ω–∏—Ü—è, –±–µ–∑ ¬´–ø—É—Å—Ç–∏—Ö¬ª –≤–µ—Ä—Ö–Ω—ñ—Ö –∑–Ω–∞—á–µ–Ω—å)
    var axisY = new LinearAxis
    {
        Position = AxisPosition.Left,
        Title = "–ß–∞—Å",
        Minimum = 0,
        Maximum = maxY,
        MajorStep = majorY,
        MinorStep = minorY,
        LabelFormatter = value =>
        {
            // value —Ç—É—Ç‚Äî—Ü–µ –≤ –æ–±—Ä–∞–Ω—ñ–π –æ–¥–∏–Ω–∏—Ü—ñ (—Å–µ–∫—É–Ω–¥–∞—Ö —á–∏ —Ö–≤–∏–ª–∏–Ω–∞—Ö —á–∏ –≥–æ–¥–∏–Ω–∞—Ö).
            if (unitLabel == "—Å")
            {
                int sec = (int)Math.Round(value);
                return $"{sec} —Å";
            }
            else if (unitLabel == "—Ö–≤.")
            {
                int min = (int)Math.Round(value);
                return $"{min} —Ö–≤.";
            }
            else // "–≥–æ–¥."
            {
                // –ó–∞–æ–∫—Ä—É–≥–ª—é—î–º–æ –º–∞–∫—Å–∏–º—É–º –¥–æ –¥–≤–æ—Ö –∑–Ω–∞–∫—ñ–≤ –ø—ñ—Å–ª—è –∫–æ–º–∏
                return $"{value:F2} –≥–æ–¥.";
            }
        }
    };
    StyleAxis(axisY);
    plotModel.Axes.Add(axisY);

    // 11) –ù–∞–º–∞–ª—é–≤–∞—Ç–∏ —Å—ñ—Ç–∫—É: –≤–µ—Ä—Ç–∏–∫–∞–ª—å–Ω—ñ –ª—ñ–Ω—ñ—ó –Ω–∞ –∫–æ–∂–Ω—É –≥–æ–¥–∏–Ω—É
    for (int h = 0; h < 24; h++)
    {
        plotModel.Annotations.Add(new LineAnnotation
        {
            Type = LineAnnotationType.Vertical,
            X = h,
            Color = Form1.settings.ColorTheme == "dark"
                        ? OxyColor.FromRgb(81, 99, 119)
                        : OxyColor.FromRgb(82, 82, 82),
            StrokeThickness = 1.5,
            LineStyle = LineStyle.Solid
        });
    }

    // –ì–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª—å–Ω—ñ –ª—ñ–Ω—ñ—ó –≤—ñ–¥ 0 –¥–æ maxY –∑ –∫—Ä–æ–∫–æ–º minorY
