        MarkerSize = 4,                    // —Ä–æ–∑–º—ñ—Ä –º–∞—Ä–∫–µ—Ä—ñ–≤ –∑–∞ –±–∞–∂–∞–Ω–Ω—è–º
        Color = Form1.settings.ColorTheme == "dark"
