    public void BuildTimeSpentByHourChart(List<UrlData> urlDataList)
    {
        // 1) Фільтрація за сьогоднішньою датою
        var today = DateTime.Today; // у вашому випадку — 07.05.2025
        var todayData = urlDataList
            .Where(d => d.Timestamp.Date == today)
