public class ChartBuilder
{
    /// <summary>
    /// Будує графік: X = година доби (0–23), Y = час проведений у годинах.
    /// Порожні години заповнюються нулями.
    /// </summary>
    public void BuildTimeSpentByHourChartSwapped(List<UrlData> urlDataList)
    {
        // Фільтрація по поточній даті (наприклад, 07.05.2025)
        var today = DateTime.Today;
        var todayData = urlDataList
            .Where(d => d.Timestamp.Date == today)
            .ToList();

        // Підготовка даних: для кожної години 0..23
        var hoursOfDay = Enumerable.Range(0, 24).ToList();
        var spentHours = hoursOfDay
            .Select(h =>
                todayData.Where(d => d.Timestamp.Hour == h).Sum(d => d.TimeSpent)
                / 3600.0
            )
            .ToList();

        // Створюємо PlotView
        var plotView = new PlotView
        {
            Dock = DockStyle.None,
            Size = new Size(600, 400),
            Location = new Point(225, 150)
        };

        var plotModel = new PlotModel
        {
            PlotAreaBorderColor = OxyColor.FromRgb(2, 14, 25),
            PlotAreaBorderThickness = new OxyThickness(1)
        };

        // Лінійна серія: X = година доби, Y = години проведені
        var lineSeries = new LineSeries
        {
            Title = "Час проведений (години)",
            MarkerType = MarkerType.Circle,
            Color = OxyColor.FromRgb(159, 183, 213)
        };

        for (int i = 0; i < hoursOfDay.Count; i++)
        {
            lineSeries.Points.Add(new DataPoint(hoursOfDay[i], spentHours[i]));
        }
        plotModel.Series.Add(lineSeries);

        // Ось X — години доби
        var axisX = new LinearAxis
        {
            Position = AxisPosition.Bottom,
            Title = "Година доби",
            TitleColor = OxyColor.FromRgb(159, 183, 213),
            TextColor = OxyColor.FromRgb(159, 183, 213),
            MajorGridlineColor = OxyColor.FromRgb(159, 183, 213),
            MinorGridlineColor = OxyColor.FromRgb(159, 183, 213),
            MajorGridlineThickness = 1,
            MinorGridlineThickness = 0.5,
            MajorStep = 1,
            MinorStep = 1,
            Minimum = -0.5,
            Maximum = 23.5,
            IsZoomEnabled = false,
            AxislineColor = OxyColor.FromRgb(159, 183, 213),
            TicklineColor = OxyColor.FromRgb(159, 183, 213)
        };

        // Ось Y — витрачений час у годинах
        var maxY = Math.Ceiling(spentHours.Max() + 0.5);
        var axisY = new LinearAxis
        {
            Position = AxisPosition.Left,
            Title = "Час проведений (години)",
            TitleColor = OxyColor.FromRgb(159, 183, 213),
            TextColor = OxyColor.FromRgb(159, 183, 213),
            MajorGridlineColor = OxyColor.FromRgb(159, 183, 213),
            MinorGridlineColor = OxyColor.FromRgb(81, 99, 119),
            MajorGridlineThickness = 1,
            MinorGridlineThickness = 0.5,
            MajorStep = 1,
            MinorStep = 0.5,
            Minimum = 0,
            Maximum = maxY,
            IsZoomEnabled = false,
            AxislineColor = OxyColor.FromRgb(159, 183, 213),
            TicklineColor = OxyColor.FromRgb(159, 183, 213)
        };

        plotModel.Axes.Add(axisX);
        plotModel.Axes.Add(axisY);

        // Додаємо додаткові сітки
        // Вертикальні (за годинами)
        for (int h = 0; h < 24; h++)
        {
            plotModel.Annotations.Add(new LineAnnotation
            {
                Type = LineAnnotationType.Vertical,
                X = h,
                Color = OxyColor.FromRgb(81, 99, 119),
                LineStyle = LineStyle.Solid,
                StrokeThickness = 1.5
            });
        }
        // Горизонтальні (за витраченим часом)
        for (double y = 0; y <= maxY; y += axisY.MinorStep)
        {
            plotModel.Annotations.Add(new LineAnnotation
            {
                Type = LineAnnotationType.Horizontal,
                Y = y,
                Color = OxyColor.FromRgb(81, 99, 119),
                LineStyle = (y % axisY.MajorStep == 0) ? LineStyle.Solid : LineStyle.Dot,
                StrokeThickness = (y % axisY.MajorStep == 0) ? 1.5 : 0.5
            });
        }

        // Додаємо PlotView на форму
        plotView.Model = plotModel;
        this.Controls.Add(plotView);
    }
}


